import{_ as t,c as o,j as p,a as r,G as a,a4 as e,B as d,o as s}from"./chunks/framework.CBloPXIQ.js";const v=JSON.parse('{"title":"VIM","description":"","frontmatter":{},"headers":[],"relativePath":"Linux/vim.md","filePath":"Linux/vim.md","lastUpdated":1728129090000}'),n={name:"Linux/vim.md"};function c(u,l,h,m,b,f){const i=d("ArticleMetadata");return s(),o("div",null,[l[0]||(l[0]=p("h1",{id:"vim",tabindex:"-1"},[r("VIM "),p("a",{class:"header-anchor",href:"#vim","aria-label":'Permalink to "VIM"'},"​")],-1)),a(i),l[1]||(l[1]=e('<p>VIM 是一个非常 NB 的编辑器，纯键盘操作，不需要鼠标，不需要方向键，减少右手离开 JKL; 这几个按键的时间，熟练后效率非常高。</p><ul><li><p>普通模式：操作文件</p></li><li><p>命令模式：输入命令</p></li><li><p>插入模式：编辑文件</p></li><li><p>可视化模式：选择文本</p></li></ul><p>普通模式尽量不使用 Backspace Delete</p><p>按 i 进入插入模式</p><p>按 Esc 进入普通模式</p><p>在普通模式下按：进入命令模式</p><p>在普通模式下按 v/V 进入可视化模式</p><p><strong>VIM 命令分小写和大写</strong></p><h2 id="移动" tabindex="-1">移动 <a class="header-anchor" href="#移动" aria-label="Permalink to &quot;移动&quot;">​</a></h2><p>在普通模式下：</p><ul><li><p>h 左移，l 右移，j 下移，k 上移</p></li><li><p>Ctrl+f 下翻页，Ctrl+b 上翻页</p></li><li><p>Ctrl+e 向下翻一行，Ctrl+y 向上翻一行</p></li><li><p>G 移动到文档末尾；gg 移动到文档开头</p></li><li><p>b 跳跃到上一个单词首字母</p></li><li><p>e 跳跃到下一个单词尾字母</p></li><li><p>w 跳跃到下一个单词首字母</p></li><li><p>W B E 跳跃大单词</p></li><li><p>Shift+6( <code>^</code> ) 跳到本行开头非空白字符</p></li><li><p>0 跳到本行开头 (包括空白字符)</p></li><li><p>Shift+4( <code>$</code> ) 跳到本行末尾字符</p></li><li><p>{ } 跳跃大括号所包含的代码块</p></li><li><p>行号 gg 跳跃到指定行</p></li></ul><h2 id="保存与退出" tabindex="-1">保存与退出 <a class="header-anchor" href="#保存与退出" aria-label="Permalink to &quot;保存与退出&quot;">​</a></h2><ul><li><p><code>:q</code> 退出</p></li><li><p><code>:wq</code> 保存退出</p></li><li><p><code>:q!</code> 不保存退出</p></li></ul><h2 id="编辑文本" tabindex="-1">编辑文本 <a class="header-anchor" href="#编辑文本" aria-label="Permalink to &quot;编辑文本&quot;">​</a></h2><p>在普通模式下：</p><ul><li><p>i 光标后插入</p></li><li><p>a 光标前插入</p></li><li><p>o 下一行插入</p></li><li><p>x 删除光标所在字符</p></li><li><p>dd 删除整行</p></li><li><p>dw 删除当前光标所在的单词</p></li><li><p>yy 复制整行</p></li><li><p>yw 复制从当前字符开始到单词末尾的文本</p></li><li><p>y$ 复制从当前字符开始到行末尾的文本</p></li><li><p>p 粘贴上次 d 或 y 操作的文本</p></li><li><p>u 撤销</p></li><li><p>r 替换字符</p></li><li><p>R 一直替换，直到按 Esc</p></li><li><p>v 从当前字符开始选择，按字符移动光标，进入可视化模式</p></li><li><p>V 从当前行开始选择，按行移动光标，进入可视化模式</p></li><li><p>Ctrl+v 矩阵选择，进入可视化模式</p></li></ul><p>在插入模式下：</p><ul><li><p>Ctrl+o 临时移动光标</p></li><li><p>Ctrl+[ 与 Esc 功能相同，退出到普通模式</p></li></ul><h2 id="选择文本" tabindex="-1">选择文本 <a class="header-anchor" href="#选择文本" aria-label="Permalink to &quot;选择文本&quot;">​</a></h2><p>在可视化模式下：</p><ul><li><p>移动所有可用</p></li><li><p>o 跳跃到选择部分的开头或末尾字符</p></li><li><p>y 复制选中部分</p></li><li><p>d 剪切选中部分</p></li><li><p>0 补全行首</p></li><li><p>全选：gg 移动到首行 &gt; v 进入选择 &gt; G 移动到行尾全选</p></li><li><p>aw 快速选择单词</p></li><li><p>ab 包含一对小括号</p></li><li><p>aB 包含一对大括号</p></li><li><p>a&gt; 包含一对尖括号</p></li><li><p>a] 包含一对方括号</p></li><li><p>at 包含一对 tag (XML 可用)</p></li><li><p>&gt; 右缩进</p></li><li><p>&lt; 左缩进</p></li><li><p>~ 切换大小写</p></li><li><p>U 全部转大写</p></li><li><p>u 全部转小写</p></li></ul><h2 id="查找替换" tabindex="-1">查找替换 <a class="header-anchor" href="#查找替换" aria-label="Permalink to &quot;查找替换&quot;">​</a></h2><p>在普通模式下</p><ul><li><p>/ 查找内容，按回车自动跳转到下一个最近的位置。按 n 移动到下一个找到的位置</p></li><li><p>😒/ 要替换的内容 / 替换成的内容 /g 将一行的内容都替换</p></li><li><p>:% s / 要替换的内容 / 替换成的内容 /g 将整个文件内容都替换</p></li><li><p>❌ys / 要替换的内容 / 替换成的内容 /g 从 x 行到 y 行全部替换</p></li><li><p>/gc 提示是否要替换</p></li></ul><h1 id="vim配置" tabindex="-1">vim 配置 <a class="header-anchor" href="#vim配置" aria-label="Permalink to &quot;vim配置&quot;">​</a></h1>',25)),a(i),l[2]||(l[2]=e("<ul><li><p>linux 下配置文件是 <code>.vimrc</code></p></li><li><p>set number 显示行号</p></li><li><p>set syntax=on 开启高亮</p></li><li><p>set tabstop=4 四空格为一个 tab</p></li><li><p>set softtabstop=4</p></li><li><p>set enc=utf-8 设置 utf-8 编码</p></li><li><p>set langmenu=zh 设置中文</p></li><li><p><code>vim .</code> 打开文件视图，找文件编辑</p></li></ul>",1))])}const x=t(n,[["render",c]]);export{v as __pageData,x as default};
